import LoadingSpinner from '../components/ui/LoadingSpinner';
import ErrorMessage from '../components/ui/ErrorMessage';
import { api } from '../services/api';

/**
 * Dashboard - Panel principal actualizado con componentes modernos
 * 
 * MEJORAS IMPLEMENTADAS:
 * - Integración con nuevos componentes UI
 * - Llamadas reales al backend FastAPI
 * - Estados de carga optimizados
 * - Error handling robusto
 * - Diseño responsive mejorado
 */

const Dashboard = () => {
  // Estados principales
  const [dashboardData, setDashboardData] = useState({
    recentAsteroids: [],
    dangerousAsteroids: [],
    upcomingApproaches: [],
    statistics: {
      total: 0,
      dangerous: 0,
      upcoming: 0,
      large: 0
    }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar datos del dashboard
  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Llamadas paralelas a tu backend FastAPI
      const [
        allAsteroidsResponse,
        dangerousResponse,
        upcomingResponse
      ] = await Promise.all([
        api.get('/asteroids/'),
        api.get('/asteroids/dangerous/'),
        api.get('/asteroids/upcoming/')
      ]);

      const allAsteroids = allAsteroidsResponse.data;
      const dangerousAsteroids = dangerousResponse.data;
      const upcomingAsteroids = upcomingResponse.data;

      // Calcular estadísticas
      const statistics = {
        total: allAsteroids.length,
        dangerous: dangerousAsteroids.length,
        upcoming: upcomingAsteroids.length,
        large: allAsteroids.filter(asteroid => {
          const minDiam = asteroid.estimated_diameter?.kilometers?.estimated_diameter_min || 
                         asteroid.estimated_diameter_km_min || 0;
          const maxDiam = asteroid.estimated_diameter?.kilometers?.estimated_diameter_max || 
                         asteroid.estimated_diameter_km_max || 0;
          const avgDiameter = (minDiam + maxDiam) / 2;
          return avgDiameter >= 1;
        }).length
      };

      setDashboardData({
        recentAsteroids: allAsteroids.slice(0, 6), // Últimos 6
        dangerousAsteroids: dangerousAsteroids.slice(0, 3), // Top 3 peligrosos
        upcomingApproaches: upcomingAsteroids.slice(0, 3), // Próximos 3
        statistics
      });

    } catch (err) {
      console.error('Error loading dashboard data:', err);
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  // Función de retry
  const handleRetry = () => {
    loadDashboardData();
  };

  // Estado de error global
  if (error && !loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
        <div className="max-w-7xl mx-auto">
          <ErrorMessage
            error={error}
            title="Error al cargar el dashboard"
            onRetry={handleRetry}
            showDetails={process.env.NODE_ENV === 'development'}
          />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header del Dashboard */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                Panel de Control
              </h1>
              <p className="mt-2 text-gray-600 dark:text-gray-400">
                Resumen de asteroides cercanos a la Tierra
              </p>
            </div>
            
            <div className="flex space-x-3">
              <Link
                to="/asteroids"
                className="
                  inline-flex items-center px-4 py-2 border border-gray-300 dark:border-gray-600
                  rounded-md shadow-sm text-sm font-medium text-gray-700 dark:text-gray-300
                  bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700
                "
              >
                Ver Todos
              </Link>
              <button
                onClick={loadDashboardData}
                disabled={loading}
                className="
                  inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm
                  text-sm font-medium text-white bg-blue-600 hover:bg-blue-700
                  focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500
                  disabled:opacity-50
                "
              >
                {loading ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Actualizando...
                  </>
                ) : (
                  'Actualizar'
                )}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Contenido principal */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 space-y-8">
        
        {/* Sección de estadísticas */}
        <section>
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
            Estadísticas Generales
          </h2>
          <StatCardGrid>
            <AsteroidStatCard
              metric="total"
              value={dashboardData.statistics.total}
              subtitle="Total en base de datos"
              loading={loading}
              onClick={() => window.location.href = '/asteroids'}
            />
            <AsteroidStatCard
              metric="dangerous"
              value={dashboardData.statistics.dangerous}
              subtitle="Clasificados como peligrosos"
              loading={loading}
              trend={dashboardData.statistics.dangerous > 0 ? 'up' : 'neutral'}
              onClick={() => window.location.href = '/asteroids?filter=dangerous'}
            />
            <AsteroidStatCard
              metric="upcoming"
              value={dashboardData.statistics.upcoming}
              subtitle="Con acercamientos próximos"
              loading={loading}
              onClick={() => window.location.href = '/asteroids?filter=upcoming'}
            />
            <AsteroidStatCard
              metric="large"
              value={dashboardData.statistics.large}
              subtitle="Diámetro ≥ 1 km"
              loading={loading}
              onClick={() => window.location.href = '/asteroids?filter=large'}
            />
          </StatCardGrid>
        </section>

        {/* Sección de asteroides peligrosos */}
        <section>
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
              🚨 Asteroides Peligrosos
            </h2>
            <Link
              to="/asteroids?filter=dangerous"
              className="text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 font-medium"
            >
              Ver todos →
            </Link>
          </div>
          
          {loading ? (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {[...Array(3)].map((_, i) => (
                <div key={i} className="animate-pulse">
                  <div className="h-48 bg-gray-200 dark:bg-gray-700 rounded-lg"></div>
                </div>
              ))}
            </div>
          ) : dashboardData.dangerousAsteroids.length > 0 ? (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {dashboardData.dangerousAsteroids.map(asteroid => (
                <AsteroidCard
                  key={asteroid.id}
                  asteroid={asteroid}
                  variant="default"
                />
              ))}
            </div>
          ) : (
            <div className="text-center py-12 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
              <div className="text-gray-400 dark:text-gray-500 mb-4">
                <svg className="mx-auto w-12 h-12" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              </div>
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                No hay asteroides peligrosos registrados
              </h3>
              <p className="text-gray-600 dark:text-gray-400">
                ¡Buenas noticias! No hay amenazas inmediatas detectadas.
              </p>
            </div>
          )}
        </section>

        {/* Sección de próximos acercamientos */}
        <section>
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
              📅 Próximos Acercamientos
            </h2>
            <Link
              to="/asteroids?filter=upcoming"
              className="text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 font-medium"
            >
              Ver calendario completo →
            </Link>
          </div>
          
          {loading ? (
            <div className="space-y-4">
              {[...Array(3)].map((_, i) => (
                <div key={i} className="animate-pulse">
                  <div className="h-24 bg-gray-200 dark:bg-gray-700 rounded-lg"></div>
                </div>
              ))}
            </div>
          ) : dashboardData.upcomingApproaches.length > 0 ? (
            <div className="space-y-4">
              {dashboardData.upcomingApproaches.map(asteroid => (
                <AsteroidCard
                  key={`upcoming-${asteroid.id}`}
                  asteroid={asteroid}
                  variant="list"
                />
              ))}
            </div>
          ) : (
            <div className="text-center py-12 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
              <div className="text-gray-400 dark:text-gray-500 mb-4">
                <svg className="mx-auto w-12 h-12" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
                </svg>
              </div>
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                No hay acercamientos programados
              </h3>
              <p className="text-gray-600 dark:text-gray-400">
                No se han detectado acercamientos próximos en los datos actuales.
              </p>
            </div>
          )}
        </section>

        {/* Sección de asteroides recientes */}
        <section>
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
              🔍 Asteroides Recientes
            </h2>
            <Link
              to="/asteroids"
              className="text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 font-medium"
            >
              Explorar catálogo →
            </Link>
          </div>
          
          {loading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {[...Array(6)].map((_, i) => (
                <div key={i} className="animate-pulse">
                  <div className="h-64 bg-gray-200 dark:bg-gray-700 rounded-lg"></div>
                </div>
              ))}
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {dashboardData.recentAsteroids.map(asteroid => (
                <AsteroidCard
                  key={`recent-${asteroid.id}`}
                  asteroid={asteroid}
                  variant="compact"
                />
              ))}
            </div>
          )}
        </section>

        {/* Footer del dashboard */}
        <section className="text-center py-8">
          <div className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-6">
            <h3 className="text-lg font-semibold text-blue-900 dark:text-blue-100 mb-2">
              🌌 Datos proporcionados por NASA
            </h3>
            <p className="text-blue-700 dark:text-blue-300 text-sm">
              La información de asteroides se obtiene de la API NeoWs de NASA.
              Última actualización: {new Date().toLocaleString('es-ES')}
            </p>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Dashboard;
