// frontend/src/components/asteroids/AsteroidCard.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

/**
 * AsteroidCard - Componente para mostrar información individual de asteroides
 * 
 * EVOLUCIÓN DEL DISEÑO DE CARDS:
 * - HTML Básico (1990s): Tables para layout
 * - CSS Float Layout (2000s): Divs flotantes
 * - CSS Flexbox (2009): Layout flexible unidimensional
 * - CSS Grid (2017): Layout bidimensional moderno
 * - Component Architecture (React 2013+): Reutilización y composición
 */

const AsteroidCard = ({ 
  asteroid, 
  variant = 'default',
  showActions = true,
  onClick,
  className = ''
}) => {
  const navigate = useNavigate();

  // Manejar datos faltantes o formatos diferentes
  const safeAsteroid = {
    id: asteroid?.id || 'unknown',
    name: asteroid?.name || 'Asteroide Desconocido',
    is_potentially_hazardous: asteroid?.is_potentially_hazardous_asteroid || asteroid?.is_potentially_hazardous || false,
    estimated_diameter_km_min: asteroid?.estimated_diameter?.kilometers?.estimated_diameter_min || asteroid?.estimated_diameter_km_min || 0,
    estimated_diameter_km_max: asteroid?.estimated_diameter?.kilometers?.estimated_diameter_max || asteroid?.estimated_diameter_km_max || 0,
    close_approach_data: asteroid?.close_approach_data || asteroid?.approaches || [],
    absolute_magnitude_h: asteroid?.absolute_magnitude_h || asteroid?.magnitude || 0,
    nasa_jpl_url: asteroid?.nasa_jpl_url || null,
    ...asteroid
  };

  // Calcular métricas derivadas
  const averageDiameter = ((safeAsteroid.estimated_diameter_km_min + safeAsteroid.estimated_diameter_km_max) / 2).toFixed(2);
  const nextApproach = safeAsteroid.close_approach_data?.[0];
  const riskLevel = getRiskLevel(safeAsteroid);

  // Función para determinar nivel de riesgo
  function getRiskLevel(asteroid) {
    let risk = 0;
    
    // Factor 1: Peligrosidad potencial
    if (asteroid.is_potentially_hazardous) risk += 40;
    
    // Factor 2: Tamaño (diámetro promedio)
    const avgDiameter = (asteroid.estimated_diameter_km_min + asteroid.estimated_diameter_km_max) / 2;
    if (avgDiameter > 1) risk += 30;
    else if (avgDiameter > 0.5) risk += 20;
    else if (avgDiameter > 0.1) risk += 10;
    
    // Factor 3: Proximidad del siguiente acercamiento
    if (nextApproach) {
      const distance = parseFloat(nextApproach.miss_distance?.kilometers || nextApproach.miss_distance || 0);
      if (distance < 1000000) risk += 20; // Menos de 1M km
      else if (distance < 5000000) risk += 10; // Menos de 5M km
    }
    
    // Factor 4: Magnitud absoluta (brillo - relacionado con tamaño)
    if (asteroid.absolute_magnitude_h < 18) risk += 10;
    
    return Math.min(risk, 100);
  }

  // Configuración de estilos según nivel de riesgo
  const getRiskStyles = (risk) => {
    if (risk >= 70) return {
      border: 'border-red-200 dark:border-red-800',
      background: 'bg-red-50 dark:bg-red-900/10',
      badge: 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200',
      indicator: 'bg-red-500'
    };
    if (risk >= 40) return {
      border: 'border-yellow-200 dark:border-yellow-800',
      background: 'bg-yellow-50 dark:bg-yellow-900/10',
      badge: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200',
      indicator: 'bg-yellow-500'
    };
    return {
      border: 'border-green-200 dark:border-green-800',
      background: 'bg-green-50 dark:bg-green-900/10',
      badge: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200',
      indicator: 'bg-green-500'
    };
  };

  const riskStyles = getRiskStyles(riskLevel);

  // Formatear fecha de acercamiento
  const formatDate = (dateString) => {
    if (!dateString) return 'Fecha desconocida';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('es-ES', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch {
      return 'Fecha inválida';
    }
  };

  // Formatear distancia
  const formatDistance = (km) => {
    if (!km) return 'Distancia desconocida';
    const distance = parseFloat(km);
    if (distance > 1000000) {
      return `${(distance / 1000000).toFixed(2)} M km`;
    }
    return `${distance.toLocaleString()} km`;
  };

  // Formatear velocidad
  const formatVelocity = (kms) => {
    if (!kms) return 'N/A';
    return `${parseFloat(kms).toFixed(1)} km/s`;
  };

  // Manejar click
  const handleClick = () => {
    if (onClick) {
      onClick(safeAsteroid);
    } else {
      navigate(`/asteroids/${safeAsteroid.id}`);
    }
  };

  // Variantes de diseño
  const variants = {
    default: 'p-6',
    compact: 'p-4',
    list: 'p-4 flex items-center space-x-4'
  };

  // Iconos SVG
  const AsteroidIcon = () => (
    <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
      <path d="M9 12a1 1 0 102 0V7a1 1 0 10-2 0v5zM6 12a1 1 0 102 0V7a1 1 0 10-2 0v5zM13 12a1 1 0 102 0V7a1 1 0 10-2 0v5z" />
    </svg>
  );

  const WarningIcon = () => (
    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
      <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
    </svg>
  );

  const ExternalLinkIcon = () => (
    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
      <path d="M11 3a1 1 0 100 2h2.586l-6.293 6.293a1 1 0 101.414 1.414L15 6.414V9a1 1 0 102 0V4a1 1 0 00-1-1h-5z" />
      <path d="M5 5a2 2 0 00-2 2v8a2 2 0 002 2h8a2 2 0 002-2v-3a1 1 0 10-2 0v3H5V7h3a1 1 0 000-2H5z" />
    </svg>
  );

  if (variant === 'list') {
    return (
      <div
        className={`
          border rounded-lg transition-all duration-200 cursor-pointer
          hover:shadow-md hover:scale-[1.02]
          ${riskStyles.border} ${riskStyles.background}
          ${variants[variant]} ${className}
        `}
        onClick={handleClick}
      >
        <div className="flex items-center justify-between">
          {/* Indicador de riesgo */}
          <div className={`w-3 h-3 rounded-full ${riskStyles.indicator}`} />
          
          {/* Información básica */}
          <div className="flex-1 ml-4">
            <h3 className="font-semibold text-gray-900 dark:text-white">
              {safeAsteroid.name}
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-400">
              Ø {averageDiameter} km
            </p>
          </div>
          
          {/* Badge de peligrosidad */}
          {safeAsteroid.is_potentially_hazardous && (
            <span className={`px-2 py-1 text-xs font-medium rounded-full ${riskStyles.badge}`}>
              <WarningIcon className="inline w-3 h-3 mr-1" />
              Peligroso
            </span>
          )}
        </div>
      </div>
    );
  }

  return (
    <div
      className={`
        border rounded-lg transition-all duration-200 cursor-pointer
        hover:shadow-lg hover:scale-[1.02]
        ${riskStyles.border} ${riskStyles.background}
        ${variants[variant]} ${className}
      `}
      onClick={handleClick}
    >
      {/* Header con nombre e indicadores */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center space-x-3">
          <div className="text-gray-600 dark:text-gray-400">
            <AsteroidIcon />
          </div>
          <div>
            <h3 className="font-bold text-lg text-gray-900 dark:text-white">
              {safeAsteroid.name}
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-400">
              ID: {safeAsteroid.id}
            </p>
          </div>
        </div>
        
        {/* Indicador de riesgo circular */}
        <div className="relative">
          <div className={`w-4 h-4 rounded-full ${riskStyles.indicator}`} />
          <span className="absolute -top-8 -right-2 text-xs font-medium text-gray-600 dark:text-gray-400">
            {riskLevel}%
          </span>
        </div>
      </div>

      {/* Métricas principales */}
      <div className="grid grid-cols-2 gap-4 mb-4">
        <div>
          <p className="text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wide">
            Diámetro
          </p>
          <p className="text-lg font-semibold text-gray-900 dark:text-white">
            {averageDiameter} km
          </p>
          <p className="text-xs text-gray-600 dark:text-gray-400">
            {safeAsteroid.estimated_diameter_km_min.toFixed(2)} - {safeAsteroid.estimated_diameter_km_max.toFixed(2)} km
          </p>
        </div>
        
        <div>
          <p className="text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wide">
            Magnitud
          </p>
          <p className="text-lg font-semibold text-gray-900 dark:text-white">
            {safeAsteroid.absolute_magnitude_h.toFixed(1)}
          </p>
          <p className="text-xs text-gray-600 dark:text-gray-400">
            Absoluta H
          </p>
        </div>
      </div>

      {/* Información de próximo acercamiento */}
      {nextApproach && (
        <div className="mb-4 p-3 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
          <p className="text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wide mb-2">
            Próximo Acercamiento
          </p>
          <div className="grid grid-cols-2 gap-2 text-sm">
            <div>
              <span className="text-gray-600 dark:text-gray-400">Fecha:</span>
              <br />
              <span className="font-medium text-gray-900 dark:text-white">
                {formatDate(nextApproach.close_approach_date)}
              </span>
            </div>
            <div>
              <span className="text-gray-600 dark:text-gray-400">Distancia:</span>
              <br />
              <span className="font-medium text-gray-900 dark:text-white">
                {formatDistance(nextApproach.miss_distance?.kilometers || nextApproach.miss_distance)}
              </span>
            </div>
            <div>
              <span className="text-gray-600 dark:text-gray-400">Velocidad:</span>
              <br />
              <span className="font-medium text-gray-900 dark:text-white">
                {formatVelocity(nextApproach.relative_velocity?.kilometers_per_second || nextApproach.velocity)}
              </span>
            </div>
            <div>
              <span className="text-gray-600 dark:text-gray-400">Órbita:</span>
              <br />
              <span className="font-medium text-gray-900 dark:text-white">
                {nextApproach.orbiting_body || 'Tierra'}
              </span>
            </div>
          </div>
        </div>
      )}

      {/* Badges y acciones */}
      <div className="flex items-center justify-between">
        <div className="flex space-x-2">
          {safeAsteroid.is_potentially_hazardous && (
            <span className={`px-2 py-1 text-xs font-medium rounded-full ${riskStyles.badge}`}>
              <WarningIcon className="inline w-3 h-3 mr-1" />
              Potencialmente Peligroso
            </span>
          )}
          
          <span className="px-2 py-1 text-xs font-medium rounded-full bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200">
            Riesgo: {riskLevel}%
          </span>
        </div>

        {/* Acciones */}
        {showActions && (
          <div className="flex space-x-1">
            {safeAsteroid.nasa_jpl_url && (
              <a
                href={safeAsteroid.nasa_jpl_url}
                target="_blank"
                rel="noopener noreferrer"
                onClick={(e) => e.stopPropagation()}
                className="p-2 text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors"
                title="Ver en NASA JPL"
              >
                <ExternalLinkIcon />
              </a>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

// Componente de loading para AsteroidCard
export const AsteroidCardSkeleton = ({ variant = 'default' }) => {
  if (variant === 'list') {
    return (
      <div className="border rounded-lg p-4 animate-pulse bg-gray-50 dark:bg-gray-800">
        <div className="flex items-center space-x-4">
          <div className="w-3 h-3 bg-gray-300 dark:bg-gray-600 rounded-full" />
          <div className="flex-1 space-y-2">
            <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-3/4" />
            <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded w-1/2" />
          </div>
          <div className="w-16 h-6 bg-gray-300 dark:bg-gray-600 rounded-full" />
        </div>
      </div>
    );
  }

  return (
    <div className="border rounded-lg p-6 animate-pulse bg-gray-50 dark:bg-gray-800">
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center space-x-3">
          <div className="w-6 h-6 bg-gray-300 dark:bg-gray-600 rounded" />
          <div className="space-y-2">
            <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded w-32" />
            <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-20" />
          </div>
        </div>
        <div className="w-4 h-4 bg-gray-300 dark:bg-gray-600 rounded-full" />
      </div>
      
      <div className="grid grid-cols-2 gap-4 mb-4">
        <div className="space-y-2">
          <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded w-16" />
          <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded w-12" />
          <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded w-20" />
        </div>
        <div className="space-y-2">
          <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded w-16" />
          <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded w-8" />
          <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded w-12" />
        </div>
      </div>
      
      <div className="h-24 bg-gray-300 dark:bg-gray-600 rounded mb-4" />
      
      <div className="flex justify-between">
        <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded w-32" />
        <div className="h-8 bg-gray-300 dark:bg-gray-600 rounded w-8" />
      </div>
    </div>
  );
};

export default AsteroidCard;
