// frontend/src/components/ui/StatCard.jsx
import React from 'react';

/**
 * StatCard - Componente para mostrar métricas y estadísticas
 * 
 * EVOLUCIÓN DE UI CARDS:
 * - Material Design (Google, 2014): Cards como superficie de contenido
 * - CSS Grid/Flexbox (2017+): Layouts responsivos nativos
 * - Tailwind CSS (2017): Utility-first, componentes modulares
 * - Dark Mode (2019+): Soporte nativo de temas
 */

const StatCard = ({
  title,
  value,
  subtitle,
  icon: Icon,
  trend,
  trendValue,
  color = 'blue',
  loading = false,
  onClick,
  className = ''
}) => {
  // Sistema de colores basado en el design system
  const colorStyles = {
    blue: {
      background: 'bg-blue-50 dark:bg-blue-900/20',
      border: 'border-blue-200 dark:border-blue-800',
      icon: 'text-blue-600 dark:text-blue-400',
      title: 'text-blue-900 dark:text-blue-100',
      value: 'text-blue-700 dark:text-blue-300',
      trend: {
        up: 'text-green-600 dark:text-green-400',
        down: 'text-red-600 dark:text-red-400',
        neutral: 'text-gray-600 dark:text-gray-400'
      }
    },
    green: {
      background: 'bg-green-50 dark:bg-green-900/20',
      border: 'border-green-200 dark:border-green-800',
      icon: 'text-green-600 dark:text-green-400',
      title: 'text-green-900 dark:text-green-100',
      value: 'text-green-700 dark:text-green-300',
      trend: {
        up: 'text-green-600 dark:text-green-400',
        down: 'text-red-600 dark:text-red-400',
        neutral: 'text-gray-600 dark:text-gray-400'
      }
    },
    red: {
      background: 'bg-red-50 dark:bg-red-900/20',
      border: 'border-red-200 dark:border-red-800',
      icon: 'text-red-600 dark:text-red-400',
      title: 'text-red-900 dark:text-red-100',
      value: 'text-red-700 dark:text-red-300',
      trend: {
        up: 'text-green-600 dark:text-green-400',
        down: 'text-red-600 dark:text-red-400',
        neutral: 'text-gray-600 dark:text-gray-400'
      }
    },
    yellow: {
      background: 'bg-yellow-50 dark:bg-yellow-900/20',
      border: 'border-yellow-200 dark:border-yellow-800',
      icon: 'text-yellow-600 dark:text-yellow-400',
      title: 'text-yellow-900 dark:text-yellow-100',
      value: 'text-yellow-700 dark:text-yellow-300',
      trend: {
        up: 'text-green-600 dark:text-green-400',
        down: 'text-red-600 dark:text-red-400',
        neutral: 'text-gray-600 dark:text-gray-400'
      }
    },
    purple: {
      background: 'bg-purple-50 dark:bg-purple-900/20',
      border: 'border-purple-200 dark:border-purple-800',
      icon: 'text-purple-600 dark:text-purple-400',
      title: 'text-purple-900 dark:text-purple-100',
      value: 'text-purple-700 dark:text-purple-300',
      trend: {
        up: 'text-green-600 dark:text-green-400',
        down: 'text-red-600 dark:text-red-400',
        neutral: 'text-gray-600 dark:text-gray-400'
      }
    }
  };

  const currentColor = colorStyles[color] || colorStyles.blue;

  // Iconos de tendencia
  const TrendUpIcon = () => (
    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
      <path fillRule="evenodd" d="M12 7a1 1 0 110-2h5a1 1 0 011 1v5a1 1 0 11-2 0V8.414l-4.293 4.293a1 1 0 01-1.414 0L8 10.414l-4.293 4.293a1 1 0 01-1.414-1.414l5-5a1 1 0 011.414 0L11 10.586 14.586 7H12z" clipRule="evenodd" />
    </svg>
  );

  const TrendDownIcon = () => (
    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
      <path fillRule="evenodd" d="M12 13a1 1 0 100 2h5a1 1 0 001-1V9a1 1 0 10-2 0v2.586l-4.293-4.293a1 1 0 00-1.414 0L8 9.586l-4.293-4.293a1 1 0 00-1.414 1.414l5 5a1 1 0 001.414 0L11 9.414 14.586 13H12z" clipRule="evenodd" />
    </svg>
  );

  const TrendNeutralIcon = () => (
    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
      <path fillRule="evenodd" d="M3 10a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd" />
    </svg>
  );

  const getTrendIcon = (trend) => {
    switch (trend) {
      case 'up': return <TrendUpIcon />;
      case 'down': return <TrendDownIcon />;
      case 'neutral': return <TrendNeutralIcon />;
      default: return null;
    }
  };

  // Skeleton loader para estado de carga
  if (loading) {
    return (
      <div className={`
        border rounded-lg p-6 
        bg-gray-50 dark:bg-gray-800 
        border-gray-200 dark:border-gray-700
        animate-pulse
        ${className}
      `}>
        <div className="flex items-center justify-between">
          <div className="space-y-2">
            <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-24"></div>
            <div className="h-8 bg-gray-300 dark:bg-gray-600 rounded w-16"></div>
            <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded w-32"></div>
          </div>
          <div className="w-12 h-12 bg-gray-300 dark:bg-gray-600 rounded-full"></div>
        </div>
      </div>
    );
  }

  const CardContent = () => (
    <div className="flex items-center justify-between">
      {/* Contenido principal */}
      <div className="flex-1">
        <h3 className={`text-sm font-medium ${currentColor.title}`}>
          {title}
        </h3>
        
        <div className={`mt-2 text-3xl font-bold ${currentColor.value}`}>
          {typeof value === 'number' ? value.toLocaleString() : value}
        </div>
        
        {subtitle && (
          <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
            {subtitle}
          </p>
        )}
        
        {/* Indicador de tendencia */}
        {trend && (
          <div className={`
            mt-2 flex items-center text-sm font-medium
            ${currentColor.trend[trend]}
          `}>
            {getTrendIcon(trend)}
            <span className="ml-1">
              {trendValue && `${trendValue} `}
              {trend === 'up' && 'Incremento'}
              {trend === 'down' && 'Disminución'}
              {trend === 'neutral' && 'Sin cambios'}
            </span>
          </div>
        )}
      </div>
      
      {/* Icono */}
      {Icon && (
        <div className={`ml-4 ${currentColor.icon}`}>
          <Icon className="w-12 h-12" />
        </div>
      )}
    </div>
  );

  // Carta clickeable vs estática
  if (onClick) {
    return (
      <button
        onClick={onClick}
        className={`
          w-full text-left border rounded-lg p-6 transition-all duration-200
          ${currentColor.background} ${currentColor.border}
          hover:shadow-lg hover:scale-105 focus:outline-none focus:ring-2 focus:ring-offset-2
          ${currentColor.icon.replace('text-', 'focus:ring-')}
          ${className}
        `}
        type="button"
      >
        <CardContent />
      </button>
    );
  }

  return (
    <div className={`
      border rounded-lg p-6 
      ${currentColor.background} ${currentColor.border}
      ${className}
    `}>
      <CardContent />
    </div>
  );
};

