// frontend/src/pages/Statistics.jsx
import React, { useState, useEffect, useMemo } from 'react';
import { 
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  PieChart, Pie, Cell, LineChart, Line, AreaChart, Area
} from 'recharts';
import LoadingSpinner from '../components/ui/LoadingSpinner';
import ErrorMessage from '../components/ui/ErrorMessage';
import StatCard, { StatCardGrid } from '../components/ui/StatCard';
import { api } from '../services/api';

/**
 * Statistics - Página de estadísticas y visualización de datos
 * 
 * EVOLUCIÓN DE VISUALIZACIÓN DE DATOS WEB:
 * - Static Charts (2000s): Imágenes generadas en servidor
 * - Flash Charts (2005): Adobe Flash para interactividad
 * - SVG + JavaScript (2010): Vectorial escalable
 * - D3.js (2011): Manipulación de DOM para visualización
 * - React + D3/Recharts (2015+): Componentes declarativos
 * - Modern Charts (2020+): Performance optimizada y responsive
 */

const Statistics = () => {
  // Estados principales
  const [asteroids, setAsteroids] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeRange, setTimeRange] = useState('all'); // 'all', 'year', 'month'

  // Cargar datos
  useEffect(() => {
    loadStatisticsData();
  }, []);

  const loadStatisticsData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Cargar todos los asteroides para análisis
      const response = await api.get('/asteroids/');
      setAsteroids(response.data);
    } catch (err) {
      console.error('Error loading statistics data:', err);
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  // Procesar datos para gráficos
  const statisticsData = useMemo(() => {
    if (!asteroids.length) return null;

    // Análisis por tamaño
    const sizeDistribution = asteroids.reduce((acc, asteroid) => {
      const minDiam = asteroid.estimated_diameter?.kilometers?.estimated_diameter_min || 
                     asteroid.estimated_diameter_km_min || 0;
      const maxDiam = asteroid.estimated_diameter?.kilometers?.estimated_diameter_max || 
                     asteroid.estimated_diameter_km_max || 0;
      const avgDiameter = (minDiam + maxDiam) / 2;

      if (avgDiameter < 0.01) acc.verySmall++;
      else if (avgDiameter < 0.1) acc.small++;
      else if (avgDiameter < 0.5) acc.medium++;
      else if (avgDiameter < 1) acc.large++;
      else acc.veryLarge++;

      return acc;
    }, { verySmall: 0, small: 0, medium: 0, large: 0, veryLarge: 0 });

    // Análisis de peligrosidad
    const dangerousCount = asteroids.filter(a => 
      a.is_potentially_hazardous_asteroid || a.is_potentially_hazardous
    ).length;
    const safeCount = asteroids.length - dangerousCount;

    // Análisis por magnitud
    const magnitudeRanges = asteroids.reduce((acc, asteroid) => {
      const magnitude = asteroid.absolute_magnitude_h || asteroid.magnitude || 0;
      
      if (magnitude < 15) acc.veryBright++;
      else if (magnitude < 18) acc.bright++;
      else if (magnitude < 21) acc.medium++;
      else if (magnitude < 24) acc.dim++;
      else acc.veryDim++;

      return acc;
    }, { veryBright: 0, bright: 0, medium: 0, dim: 0, veryDim: 0 });

    // Análisis temporal de acercamientos
    const approachsByYear = {};
    asteroids.forEach(asteroid => {
      const approaches = asteroid.close_approach_data || asteroid.approaches || [];
      approaches.forEach(approach => {
        const date = new Date(approach.close_approach_date || approach.date);
        const year = date.getFullYear();
        if (year >= 2020 && year <= 2030) { // Rango relevante
          approachsByYear[year] = (approachsByYear[year] || 0) + 1;
        }
      });
    });

    // Preparar datos para gráficos
    const sizeChartData = [
      { name: 'Muy Pequeños\n(< 0.01 km)', value: sizeDistribution.verySmall, color: '#3B82F6' },
      { name: 'Pequeños\n(0.01-0.1 km)', value: sizeDistribution.small, color: '#10B981' },
      { name: 'Medianos\n(0.1-0.5 km)', value: sizeDistribution.medium, color: '#F59E0B' },
      { name: 'Grandes\n(0.5-1 km)', value: sizeDistribution.large, color: '#EF4444' },
      { name: 'Muy Grandes\n(> 1 km)', value: sizeDistribution.veryLarge, color: '#8B5CF6' }
    ];

    const dangerChartData = [
      { name: 'Seguros', value: safeCount, color: '#10B981' },
      { name: 'Potencialmente Peligrosos', value: dangerousCount, color: '#EF4444' }
    ];

    const magnitudeChartData = [
      { name: 'Muy Brillantes (< 15)', count: magnitudeRanges.veryBright },
      { name: 'Brillantes (15-18)', count: magnitudeRanges.bright },
      { name: 'Medios (18-21)', count: magnitudeRanges.medium },
      { name: 'Tenues (21-24)', count: magnitudeRanges.dim },
      { name: 'Muy Tenues (> 24)', count: magnitudeRanges.veryDim }
    ];

    const yearlyApproachData = Object.entries(approachsByYear)
      .map(([year, count]) => ({ year: parseInt(year), approaches: count }))
      .sort((a, b) => a.year - b.year);

    return {
      total: asteroids.length,
      dangerous: dangerousCount,
      safe: safeCount,
      sizeDistribution,
      magnitudeRanges,
      sizeChartData,
      dangerChartData,
      magnitudeChartData,
      yearlyApproachData
    };
  }, [asteroids]);

  // Colores para gráficos
  const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#F97316', '#06B6D4'];

  // Componente de tooltip personalizado
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white dark:bg-gray-800 p-3 border border-gray-200 dark:border-gray-700 rounded-lg shadow-lg">
          <p className="font-medium text-gray-900 dark:text-white">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} style={{ color: entry.color }} className="text-sm">
              {`${entry.dataKey}: ${entry.value}`}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  // Función de retry
  const handleRetry = () => {
    loadStatisticsData();
  };

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
        <div className="max-w-7xl mx-auto">
          <ErrorMessage
            error={error}
            title="Error al cargar estadísticas"
            onRetry={handleRetry}
            showDetails={process.env.NODE_ENV === 'development'}
          />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                📊 Estadísticas de Asteroides
              </h1>
              <p className="mt-2 text-gray-600 dark:text-gray-400">
                Análisis detallado de la base de datos de NEOs
              </p>
            </div>
            
            <div className="flex items-center space-x-4">
              {/* Selector de rango temporal */}
              <select
                value={timeRange}
                onChange={(e) => setTimeRange(e.target.value)}
                className="
                  px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md
                  bg-white dark:bg-gray-700 text-gray-900 dark:text-white
                  focus:outline-none focus:ring-2 focus:ring-blue-500
                "
              >
                <option value="all">Todos los datos</option>
                <option value="year">Último año</option>
                <option value="month">Último mes</option>
              </select>
              
              <button
                onClick={loadStatisticsData}
                disabled={loading}
                className="
                  inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm
                  text-sm font-medium text-white bg-blue-600 hover:bg-blue-700
                  focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500
                  disabled:opacity-50
                "
              >
                {loading ? 'Actualizando...' : 'Actualizar'}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Contenido principal */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <LoadingSpinner size="xl" text="Generando estadísticas..." />
          </div>
        ) : statisticsData ? (
          <div className="space-y-8">
            
            {/* Métricas generales */}
            <section>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
                Resumen General
              </h2>
              <StatCardGrid>
                <StatCard
                  title="Total de Asteroides"
                  value={statisticsData.total.toLocaleString()}
                  subtitle="En la base de datos"
                  color="blue"
                  icon={({ className }) => (
                    <svg className={className} fill="currentColor" viewBox="0 0 20 20">
                      <path d="M9 12a1 1 0 102 0V7a1 1 0 10-2 0v5zM6 12a1 1 0 102 0V7a1 1 0 10-2 0v5zM13 12a1 1 0 102 0V7a1 1 0 10-2 0v5z" />
                    </svg>
                  )}
                />
                
                <StatCard
                  title="Peligrosos"
                  value={statisticsData.dangerous.toLocaleString()}
                  subtitle={`${((statisticsData.dangerous / statisticsData.total) * 100).toFixed(1)}% del total`}
                  color="red"
                  trend="up"
                  icon={({ className }) => (
                    <svg className={className} fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                  )}
                />
                
                <StatCard
                  title="Seguros"
                  value={statisticsData.safe.toLocaleString()}
                  subtitle={`${((statisticsData.safe / statisticsData.total) * 100).toFixed(1)}% del total`}
                  color="green"
                  trend="neutral"
                  icon={({ className }) => (
                    <svg className={className} fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                    </svg>
                  )}
                />
                
                <StatCard
                  title="Grandes (> 1 km)"
                  value={statisticsData.sizeDistribution.veryLarge.toLocaleString()}
                  subtitle="Asteroides de gran tamaño"
                  color="purple"
                  icon={({ className }) => (
                    <svg className={className} fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M3 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd" />
                    </svg>
                  )}
                />
              </StatCardGrid>
            </section>

            {/* Gráficos principales */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              
              {/* Distribución por tamaño */}
              <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Distribución por Tamaño
                </h3>
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={statisticsData.sizeChartData}
                      cx="50%"
                      cy="50%"
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                      label={({ name, percent }) => `${(percent * 100).toFixed(0)}%`}
                    >
                      {statisticsData.sizeChartData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip content={<CustomTooltip />} />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>

              {/* Peligrosidad */}
              <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Clasificación de Peligrosidad
                </h3>
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={statisticsData.dangerChartData}
                      cx="50%"
                      cy="50%"
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                      label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(1)}%`}
                    >
                      {statisticsData.dangerChartData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip content={<CustomTooltip />} />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Gráfico de magnitud */}
            <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                Distribución por Magnitud Absoluta
              </h3>
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={statisticsData.magnitudeChartData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis 
                    dataKey="name" 
                    angle={-45}
                    textAnchor="end"
                    height={80}
                  />
                  <YAxis />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar dataKey="count" fill="#3B82F6" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            {/* Acercamientos por año */}
            {statisticsData.yearlyApproachData.length > 0 && (
              <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Acercamientos por Año (2020-2030)
                </h3>
                <ResponsiveContainer width="100%" height={400}>
                  <AreaChart data={statisticsData.yearlyApproachData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="year" />
                    <YAxis />
                    <Tooltip content={<CustomTooltip />} />
                    <Area 
                      type="monotone" 
                      dataKey="approaches" 
                      stroke="#10B981" 
                      fill="#10B981" 
                      fillOpacity={0.3}
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            )}

            {/* Datos detallados */}
            <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                Análisis Detallado
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                
                {/* Distribución de tamaños */}
                <div>
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                    Por Tamaño
                  </h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Muy pequeños:</span>
                      <span className="font-medium">{statisticsData.sizeDistribution.verySmall}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Pequeños:</span>
                      <span className="font-medium">{statisticsData.sizeDistribution.small}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Medianos:</span>
                      <span className="font-medium">{statisticsData.sizeDistribution.medium}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Grandes:</span>
                      <span className="font-medium">{statisticsData.sizeDistribution.large}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Muy grandes:</span>
                      <span className="font-medium">{statisticsData.sizeDistribution.veryLarge}</span>
                    </div>
                  </div>
                </div>

                {/* Análisis de riesgo */}
                <div>
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                    Análisis de Riesgo
                  </h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Seguros:</span>
                      <span className="font-medium text-green-600">{statisticsData.safe}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Peligrosos:</span>
                      <span className="font-medium text-red-600">{statisticsData.dangerous}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">% Peligrosos:</span>
                      <span className="font-medium">
                        {((statisticsData.dangerous / statisticsData.total) * 100).toFixed(2)}%
                      </span>
                    </div>
                  </div>
                </div>

                {/* Información adicional */}
                <div>
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                    Información Adicional
                  </h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Última actualización:</span>
                      <span className="font-medium">{new Date().toLocaleDateString('es-ES')}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Fuente de datos:</span>
                      <span className="font-medium">NASA NeoWs</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600 dark:text-gray-400">Período analizado:</span>
                      <span className="font-medium">2020-2030</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="text-center py-12">
            <p className="text-gray-600 dark:text-gray-400">
              No hay datos disponibles para generar estadísticas.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Statistics;
