// frontend/src/App.jsx - VERSI√ìN UNIFICADA SIN DOBLE NAVEGACI√ìN
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import UnifiedLayout from './components/layout/UnifiedLayout';
import Dashboard from './pages/Dashboard';
import AsteroidListPage from './pages/AsteroidListPage';
import AsteroidDetail from './pages/AsteroidDetail';
import Statistics from './pages/Statistics';
import { ErrorBoundary } from './components/ui/ErrorMessage';

/**
 * App - Componente principal OPTIMIZADO
 * 
 * MEJORAS:
 * ‚úÖ Layout unificado sin doble navegaci√≥n
 * ‚úÖ Dise√±o espacial consistente
 * ‚úÖ Error boundaries modernos
 * ‚úÖ Routing optimizado
 */

function App() {
  return (
    <ErrorBoundary>
      <Router>
        <UnifiedLayout>
          <Routes>
            {/* Ruta principal - Dashboard */}
            <Route path="/" element={<Dashboard />} />
            
            {/* Rutas de asteroides */}
            <Route path="/asteroids" element={<AsteroidListPage />} />
            <Route path="/asteroids/:id" element={<AsteroidDetail />} />
            
            {/* Ruta de estad√≠sticas */}
            <Route path="/statistics" element={<Statistics />} />
            
            {/* Ruta de dashboard alternativa */}
            <Route path="/dashboard" element={<Navigate to="/" replace />} />
            
            {/* Ruta 404 - Not Found */}
            <Route path="*" element={<NotFoundPage />} />
          </Routes>
        </UnifiedLayout>
      </Router>
    </ErrorBoundary>
  );
}

// Componente 404 optimizado con dise√±o espacial
const NotFoundPage = () => {
  return (
    <div className="flex items-center justify-center min-h-[60vh]">
      <div className="max-w-md w-full text-center">
        {/* Animaci√≥n espacial */}
        <div className="mb-8 relative">
          <div className="text-9xl font-bold text-slate-700/30 mb-4">404</div>
          <div className="text-6xl mb-4 animate-bounce">üåå</div>
          {/* Efecto de √≥rbita sutil */}
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
            <div className="w-32 h-32 border border-blue-500/20 rounded-full animate-spin" style={{animationDuration: '8s'}}></div>
          </div>
        </div>
        
        <h2 className="text-3xl font-bold text-white mb-4">
          P√°gina perdida en el espacio
        </h2>
        
        <p className="text-slate-400 mb-8">
          La p√°gina que buscas no existe o fue desintegrada por un asteroide.
        </p>
        
        <div className="space-y-3">
          <a
            href="/"
            className="
              block w-full btn-stellar text-center
            "
          >
            üè† Volver al Dashboard
          </a>
          
          <a
            href="/asteroids"
            className="
              block w-full btn-ghost text-center
            "
          >
            üîç Explorar Asteroides
          </a>
        </div>
      </div>
    </div>
  );
};

export default App;
